import { Component, OnInit, ViewChild } from '@angular/core';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { ActivatedRoute } from '@angular/router';
import { switchMap } from 'rxjs/operators';
import { Book } from 'src/app/Models/book-model';
import { HomeService } from 'src/app/services/home.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {

  constructor(
    private route: ActivatedRoute,
    private service: HomeService
  )
  { 
    this.service.listen().subscribe((m:any)=>{
      this.refreshMostSoldBookList();
    });
  }

  listData: MatTableDataSource<any> = new MatTableDataSource<any>();
  displayedColumns : string[] = ['bookId', 'bookTitle', 'bookAuthorName', 'bookDescription', 'category', 'bookRating', 'bookNumberOfRatings', 'bookPrice'];

  @ViewChild(MatSort) sort: MatSort;

  public mostSoldBooks: Book[] = [];
  public bookTitle: Array<string> = [];

  async ngOnInit(): Promise<void> {
    await this.refreshMostSoldBookList();
    console.log(this.mostSoldBooks);
    console.log(this.bookTitle);
  }


  refreshMostSoldBookList() {
    this.service.mostSoldBooks$.pipe(switchMap(
      (data: Book[]) => {
        this.mostSoldBooks = data;
        this.bookTitle.push(data[0].bookTitle);
        this.bookTitle.push(data[1].bookTitle);
        this.bookTitle.push(data[2].bookTitle);
        console.log(this.mostSoldBooks[0]);
        return this.route.queryParams;
      }
    )).subscribe();
  }
